window.addEventListener("DOMContentLoaded",(()=>{let e=document.querySelector(".menu__icon"),t=document.querySelector(".nav");e&&e.addEventListener("click",(()=>{document.body.classList.toggle("lock"),e.classList.toggle("_active-icon"),t.classList.toggle("_active")}));const c=document.querySelectorAll('a[href*="#"]');for(let o of c){o.addEventListener("click",(function(c){c.preventDefault(),e.classList.contains("_active-icon")&&(document.body.classList.remove("lock"),e.classList.remove("_active-icon"),t.classList.remove("_active"));const n=o.getAttribute("href").substr(1);document.getElementById(n).scrollIntoView({behavior:"smooth",block:"start"})}));let c=document.querySelector(".language"),n=document.querySelector(".header__link-ru");c.addEventListener("click",(()=>{n.classList.toggle("_active")}))}}));